    async Task AddMeal()
    {
        var mealModal = Modal.Show<AddMeal>("Ajout de plat");
        var result = await mealModal.Result;

        if (!result.Cancelled)
        {
            meals = await MealService.GetMealsAsync();
        }
    }

    async Task DeleteMeal(int id)
